INSERT INTO `isachnoi` (`ID`, `Ten`, `AnhBia`, `TomTat`, `TacGia`, `BienTap`, `TheLoai`, `MucLuc`, `NguoiDoc`, `LinkDown`, `NgayTao`, `NgayCapNhat`) VALUES
(1, 'Chiến thăng trò chơi cuộc sống', 'http://sachnoionline.net/upload/book/2894.jpg', 'la sách thôi', 19, 10, 31, '[01].Chương 1: Thành công là một cuộc hành trình\r\n[02].Chương 2: Lần theo dấu vết của thành công và Chương 3: Đặt ra tiêu chuẩn cao\r\n[03].Chương 4: Cái giá để trở nên tài năng là 10.000 giờ và Chương 5: Gặp may\r\n[04].Chương 6: Dám chấp nhận rủi ro và Chương 7: Gần mực thì đen – Gần đèn thì sáng (A)\r\n[05].Chương 7: Gần mực thì đen – Gần đèn thì sáng (B) và Chương 8: Tận dụng sức mạnh đòn bẩy để thành công\r\n[06].Chương 9: Làm chủ thế giới nội tại trong bạn và Chương 10: Thay đổi cách nhìn nhận bản thân để thay đổi\r\n[07].Chương 11: Rèn luyện những thói quen đưa đến thành công và Chương 12: Hãy để cảm xúc phục vụ bạn\r\n[08].Chương 13: Phát triển hoặc chấp nhận chết mòn và Chương 14: Nuôi dưỡng tí tuệ và tâm hồn mỗi ngày\r\n[09].Chương 15: Phần thưởng cao quý nhất chỉ dành cho người bền chí\r\n[10].Chương 16: Những món quà đến từ ngịch cảnh\r\n[11].Chương 17: Hãy yêu người quan trọng nhất cuộc đời bạn\r\n[12].Chương 18: Học cách biết ơn cuộc sống và Chương 19: Chìa khóa mở ra hạnh phúc dài lâu (Hết)', 2, 'http://sachnoionline.net/sach/2894/chien-thang-tro-choi-cuoc-song', '2017-11-20', '2017-11-06'),
(2, 'Làng', 'http://isach.info/images/story/cover/lang__kim_lan.jpg', 'truyện làng', 2, 4, 11, 'nhiều chương', 3, 'https://youtu.be/LIfSWUmvKD8', '2017-11-07', '2017-11-25'),
(3, 'BÍ QUYẾT TỰ THÂN LÀM GIÀU', 'http://sachnoionline.net/upload/book/524.jpg', 'GIỚI THIỆU SƠ LƯỢC\r\nShaun Rein, người sáng lập hãng nghiên cứu thị trường Trung Quốc, từng có cơ hội tiếp cận một tỷ phú Trung Quốc. Anh gọi tỷ phú đó là ông Chen, chỉ đơn giản như vậy. Nhưng những lần chuyện trò với ông Chen, đã mang lại cho Rein những bài học đáng quý. Và giờ đây, anh muốn chia sẻ lại với bạn đọc những kinh nghiệm này, thông qua bài viết đăng trên tờ Forbes.', 29, 11, 31, '', 2, 'http://sachnoionline.net/sach/524/bi-quyet-tu-than-lam-giau', '2017-11-05', '2017-11-23'),
(4, 'Cô bé bán diêm', 'http://sachnoionline.net/upload/book/1906.jpg', 'Cô bé bán diêm sống trong gia đình nghèo khổ, khó khăn, mồ côi mẹ, bà mất sớm, tài sản tiêu tan nên em phải bán diêm cho người bố rất tàn nhẫn hay đánh em. Vào một ngày cuối năm, em không bán được que diêm nào. Trước khi ra khỏi nhà em có mang 1 đôi giày mà mẹ em để lại nhưng nó lại quá to. 1 chiếc giày đã bị xe ngựa cán qua và chiếc còn lại bị 1 thằng bé xấu tính ném mất. Em không dám về nhà vì sợ cha đánh. Đêm giao thừa trời giá rét, em ngồi nép vào góc tường giữa hai ngôi nhà. Đêm càng lạnh giá, em đánh liều quẹt que diêm để sưởi ấm', 30, 9, 9, '', 1, '<p>Cô bé bán diêm sống trong gia đình nghèo khổ, khó khăn, mồ côi mẹ, bà mất sớm, tài sản tiêu tan nên em phải bán diêm cho người bố rất tàn nhẫn hay đánh em. Vào một ngày cuối năm, em không bán được que diêm nào. Trước khi ra khỏi nhà em có mang 1 đôi giày mà mẹ em để lại nhưng nó lại quá to. 1 chiếc giày đã bị xe ngựa cán qua và chiếc còn lại bị 1 thằng bé xấu tính ném mất. Em không dám về nhà vì sợ cha đánh. Đêm giao thừa trời giá rét, em ngồi nép vào góc tường giữa hai ngôi nhà. Đêm càng lạnh giá, em đánh liều quẹt que diêm để sưởi ấm</p>', '2017-08-06', '2017-09-29');
