INSERT INTO `sachtruyenthong` (`ID`, `Ten`, `AnhBia`, `TomTat`, `TacGia`, `BienTap`, `TheLoai`, `TrangThai`, `NgayXuatBan`, `NgayTao`, `NgayCapNhat`) VALUES
(1, 'Nỗi buồn chiến tranh', 'https://tiendathoang.com/blog/wp-content/uploads/2014/06/noi-buon-chien-tranh.jpg', 'được ca tụng rộng rãi, và một số nhà phê bình đánh giá là một trong những tiểu thuyết cảm động nhất về chiến tranh. Bản dịch này được photo bán rộng rãi cho du khách nước ngoài. Đây là một cuốn sách được đọc rộng rãi ở phương Tây, và là một trong số ít sách nói về chiến tranh từ quan điểm phía Việt Nam được xuất bản ở đây', 18, 3, 1, 1, '2017-11-07', '2017-11-22', '2017-11-08'),
(2, 'Chí phèo', 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/42/Chi_Pheo_cover_1957.jpg/260px-Chi_Pheo_cover_1957.jpg', 'Chí Phèo là một truyện ngắn nổi tiếng của nhà văn Nam Cao viết vào tháng 2 năm 1941. Chí Phèo là một tác phẩm xuất sắc, thể hiện nghệ thuật viết truyện độc đáo của Nam Cao, đồng thời là một tấn Bi kịch của một người nông dân nghèo bị tha hóa trong xã hội. Hiện nay, truyện đã được đưa vào sách giáo khoa Ngữ văn 11, tập 1. Chí Phèo cũng là tên nhân vật chính của truyện.', 7, 6, 1, 1, '2017-11-07', '2017-11-22', '2017-11-08'),
(3, 'Ai và Ky ở xứ sở các con số tàng hình', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRpT6dNVz2vSDBrvcPghipU04d6cksrG-uXYtwAcwomMz8foFPhQA', 'Ai và Ky ở xứ sở những con số tàng hình là tên một tiểu thuyết viết về toán học dành cho thiếu nhi xuất bản năm 2012 của hai đồng tác giả là Giáo sư Ngô Bảo Châu và Blogger Nguyễn Phương Văn. Truyện được hai tác giả gọi vui là \"Tiểu thuyết Toán hiệp\".[1]. Từ ý tưởng viết truyện về lịch sử toán học, sau đó được hai tác giả phát triển thành một câu chuyện sinh động', 4, 3, 14, 0, '2017-11-07', '2017-11-22', '2017-11-08'),
(4, 'Sóng', 'https://i.ytimg.com/vi/nVAEfXBzR0M/maxresdefault.jpg', 'Sắc điệu trữ tình của bài thơ được gợi lên từ hình tượng sóng. Cả bài thơ là những con sóng tâm tình xôn xao trong lòng người con gái đang yêu khi đứng trước biển ngắm nhìn những con sóng vô hồi, bất tận', 10, 9, 16, 1, '2017-11-07', '2017-11-22', '2017-11-08'),
(5, 'Qua đèo ngang', 'https://donghuongkontum.files.wordpress.com/2016/08/2a27d-13339666_1373115032705152_636070628980456883_n.jpg', 'ảnh Đèo Ngang được miêu tả gồm những chi tiết: cỏ cây, hoa lá, dãy núi, con sông, cái chợ, mấy túp nhà, tiếng chim quốc, chim đa đa, có vài chú tiều phu. Các chi tiết này cho thấy cảnh Đèo Ngang um tùm, rậm rạp. Con người thì ít ỏi, thưa thớt.', 11, 10, 17, 1, '2017-11-07', '2017-11-22', '2017-11-08'),
(6, 'Vang bóng một thời', 'https://v2.tikicdn.com/cache/200x200/media/catalog/product/v/a/vang-bong-mot-thoi.jpg', 'Người ta thường chú ý đến những con nguời đựoc miêu tả trong \"Vang bóng một thời\": ông Huấn Cao, cụ Tú Lan,ông cử Hai, vợ chồng ông phó sứ... Đó là những con người tài hoa, có vẻ đẹp thanh cao và cuộc sống vượt khỏi những bó buộc của danh lợi thường tình.Họ là hiện thân của vẻ đẹp xưa mà Nguyễn Tuân say mê kiếm tìm, chiêm ngưỡng, ngợi ca bằng tất cả tình yêu, niềm kính phục. ', 6, 1, 1, 1, '2017-11-27', '2017-11-29', '2017-11-30'),
(7, 'Tin học cơ bản', 'https://www.dichthuatchaua.com/images/dichthuatchaua/dichsachtinhocvienthong.jpg', 'giúp tìm hiểu căn bản về tin học, cài đặt và sử dụng microsoft world 2007', 36, 10, 6, 1, '2017-10-08', '2017-10-16', '2017-10-18'),
(8, 'Phân tích dữ liệu với R', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgDyPbTzASIlOKx3gpT4EpeKsjhb-Xp-dSb8kO6L5vWao3jMr0MA', 'phân tích dữ liệu với R', 37, 6, 6, 1, '2017-10-01', '2017-10-10', '2017-11-27'),
(9, 'Sinh tồn trong mọi hoàn cảnh', 'https://mensach.files.wordpress.com/2014/11/nhung-cach-thong-minh-de-sinh-ton-trong-moi-hoan-canh-ki-nang-di-da-ngoai.jpg', 'giúp biết cách tồn tại trong các hoàn cảnh khó khăn', 38, 10, 25, 1, '2017-11-05', '2017-11-22', '2017-11-29'),
(10, 'Giải tích 1', 'http://nxbthongtintruyenthong.vn/vi/0/product/Image?url=%2Ffiles%2Fproduct%2Fgiao%20trinh%20giai%20tich%201.jpg', 'giúp giải toán dễ dàng hơn', 39, 8, 20, 1, '2017-11-06', '2017-11-15', '2017-11-29');

