INSERT INTO `tacgia` (`ID`, `Ten`, `NgaySinh`, `GioiTinh`, `SDT`, `TieuSu`) VALUES
(1, 'Nguyễn Nhật Ánh', '1987-11-15', 0, 972569875, 'Nguyễn Nhật Ánh là nhà văn Việt Nam chuyên viết cho tuổi mới lớn. Ông sinh ngày 7 tháng 5 năm 1955 tại thôn An Mỹ, xã Bình An, huyện Thăng Bình, tỉnh Quảng Nam'),
(2, 'Kim Lân', '1920-08-01', 1, 0, 'Kim Lân, tên thật là Nguyễn Văn Tài, là một nhà văn Việt Nam. Ông được biết đến với các tác phẩm văn học như Vợ nhặt, Làng.'),
(4, 'Ngô Bảo Châu', '1988-02-03', 1, 987654784, 'Nhà toán học người Việt Nam'),
(6, 'Nguyễn Tuân', '1910-07-10', 1, 9876782313, 'là nhà văn nổi tiếng'),
(7, 'Nam Cao', '1915-11-15', 1, 972569875, 'là một nhà văn hiện thực lớn (trước Cách mạng), một nhà báo kháng chiến (sau Cách mạng), một trong những nhà văn tiêu biểu nhất thế kỷ 20 của Việt Nam. Ông có nhiều đóng góp quan trọng đối với việc hoàn thiện phong cách truyện ngắn và tiểu thuyết Việt Nam ở nửa đầu thế kỷ 20.'),
(9, 'Nguyên Hồng', '1918-11-05', 1, 972569875, 'Nguyên Hồng ham đọc sách từ nhỏ. Ông thường dành tiền thuê sách để đọc và dường như đọc hết những quyển sách mình thích ở cửa hàng cho thuê sách tại Nam Định. Loại sách Nguyên Hồng thích thuở nhỏ là truyện lịch sử Trung Hoa, trong đó những nhân vật có khí phách ngang tàng, trung dũng, những hảo hán chiếm cảm tình của ông nhiều nhất.'),
(10, 'Xuân Quỳnh', '1942-01-02', 0, 972569875, ' là một nhà thơ nữ Việt Nam. Bà được xem là nữ thi sĩ nổi tiếng với nhiều bài thơ được nhiều người biết đến như Thuyền và Biển, Sóng, Thơ tình cuối mùa thu, Tiếng gà trưa... Bà được nhà nước Việt Nam truy tặng Giải thưởng Nhà nước và Giải thưởng Hồ Chí Minh vì những thành tựu cho nền văn học nước nhà.'),
(11, 'Hồ Xuân Hương', '1972-11-15', 0, 972569875, 'à một thi sĩ sống ở giai đoạn cuối thế kỷ XVIII và đầu thế kỷ XIX, mà hành trạng hiện vẫn khiến nhiều học giả tranh cãi[1]. Di tác của bà hoàn toàn là thơ, trong đó mảng Nôm có nhiều phẩm chất tốt cần sự khảo cứu lâu, nhưng hậu thế có thi sĩ Xuân Diệu đã mạo muội tôn Hồ Xuân Hương làm Bà chúa thơ Nôm'),
(12, 'Tạ Quang Bửu', '1910-07-10', 1, 9876782313, 'Tạ Quang Bửu là giáo sư, nhà khoa học nổi tiếng của Việt Nam, ông là người tiên phong trong lĩnh vực khoa học kỹ thuật và công nghệ quân sự Việt Nam. Ông đã đề xuất cải tiến nội dung giảng dạy những điều cơ bản nhất, hiện đại nhất và sát hợp với điều kiện Việt Nam nhất ở tất cả bộ môn và ngành đào tạo. Các công trình tiêu biểu của ông là về phát triển khoa học cơ bản, kỹ thuật rà phá bom mìn trong chiến tranh.'),
(14, 'Nguyễn Thục Quyên', '1970-11-15', 0, 972569875, 'Nguyễn Thục Quyên là nhà khoa học nữ nổi tiếng duy nhất của Việt Nam. Bà là một nhà khoa học, nhà nghiên cứu người Mỹ gốc Việt. Bà hiện đang là giảng viên khoa Hóa – Sinh của ĐH California Santa Barbara. Bà trở nên nổi tiếng trong các nhà khoa học khi xuất sắc giành được giải Alfred Sloan quý giá. Các nghiên cứu của bà đều về các dụng cụ điện tử hữu cơ như: quang điện, lep, pin mặt trời…'),
(18, 'Bảo Ninh', '1922-11-12', 1, 4242515134, 'sinh tại huyện Diễn Châu, tỉnh Nghệ An, quê ở xã Bảo Ninh, huyện Quảng Ninh, tỉnh Quảng Bình, Việt Nam. Ông là con trai của Giáo sư Hoàng Tuệ (1922 - 1999), nguyên Viện trưởng Viện Ngôn ngữ học. Ông vào bộ đội năm 1969. Thời chiến tranh, ông chiến đấu ở mặt trận B-3 Tây Nguyên, tại tiểu đoàn 5, trung đoàn 24, sư đoàn 10. Năm 1975, ông giải ngũ. Từ 1976-1981 học đại học ở Hà Nội, sau đó làm việc ở Viện Khoa học Việt Nam. Từ 1984-1986 học khoá 2 Trường viết văn Nguyễn Du. Làm việc tại báo Văn nghệ Trẻ. Là hội viên Hội Nhà văn Việt Nam từ 1997.'),
(19, 'Adam Khoo', '1974-11-05', 1, 1, ' một doanh nhân thành đạt, tác giả của những quyển sách bán chạy nhất và cũng là chuyên gia đào tạo hàng đầu'),
(20, 'Trần Việt Thanh', '1972-11-06', 0, 956743232, 'sinh ra và lớn lên ở Thanh Hóa, Giỏi tiếng nhật từ nhỏ.'),
(21, 'Vũ Thanh Phương', '1988-11-06', 0, 1654678987, 'Giỏi ngoại ngữ'),
(22, 'Vũ Trọng Phụng', '1977-11-12', 1, 921321543, 'Là nhà văn học Việt Nam'),
(23, 'J. R. R. Tolkien', '1982-01-05', 1, 12334265, ' Tolkien là bạn thân của C. S. Lewis; cả hai cùng là thành viên của nhóm văn sĩ nổi tiếng Inklings'),
(24, 'Đằng Qua', '1957-11-13', 1, 432432455, 'tôi mê viết, mê tình tiết, mê nhân vật, và mê cả độc giả. Tôi thích trốn vào một góc, lặng lẽ kể ra một vài câu chuyện của chính mình, một vài tình tiết không bị ai biết, và những rung động nảy sinh từ đó.\r\nCác con chữ lơ đễnh, song nếu không ghi lại, chỉ sợ rằng mình sẽ nhanh chóng quên đi cảm xúc trong khoảnh khắc ấy.\r\nBiết rõ không thể, nhưng đời này vẫn ước mong, được thỏa nguyện tùy ý ngắm biển cả ngắm non ngàn ngắm nhân gian…'),
(25, 'Steve Suehring', '1933-06-13', 1, 124435421, 'Chuyên lập trình các ứng dụng website php'),
(26, 'Nguyễn Huy Dung', '1931-06-11', 1, 45435225245, 'Tốt nghiệp bác sĩ tại Đại học Moskva Pirogov năm 1959. Bảo vệ Tiến sĩ tại Viện Tim của Viện Hàn Lâm Khoa học Y toàn Liên Xô (cũ) năm 1963. Được phong học hàm Phó Giáo sư năm 1984, Giáo sư Y học năm 1991.'),
(27, 'Donato Carrisi', '1973-09-25', 0, 6143765756, 'là một nhà văn , nhà biên kịch , nhà viết kịch , nhà báo và đạo diễn Ý , người đoạt giải Bancarella năm 2009 với The Culver .'),
(28, 'Donald Trump', '1946-06-19', 1, 2, 'là đương kim Tổng thống Hoa Kỳ, và là tổng thống thứ 45. Trước đó, ông nổi tiếng với vai trò là một tỷ phú doanh nhân, nhà sản xuất chương trình truyền hình.\r\n\r\nLà chủ tịch kiêm tổng giám đốc của The Trump Organization, Trump cũng đồng thời là người sáng lập Trump Entertainment Resorts. Ông nổi tiếng trên toàn nước Mỹ nhờ sự nghiệp, nỗ lực gây dựng thương hiệu, đời sống cá nhân, sự giàu có và bản tính thẳng thắn của mình. Ngày 9 tháng 11 năm 2016, trong cuộc bầu cử tổng thống Hoa Kỳ 2016, ông đã chiến thắng ứng cử viên Hillary Clinton của Đảng Dân chủ để trở thành Tổng thống thứ 45 của Hoa Kỳ.'),
(29, 'Scott Witt', '1944-08-06', 0, 3432432423, 'làm giàu đi lên từ sức mình'),
(30, 'Hans Christian Andersen', '1805-11-06', 1, 213213123123, 'An-đéc-xen(1805-1875) là nhà văn Đan Mạch nổi tiếng với truyện kể cho trẻ em. Những tác phẩm của ông đã trở nên quen thuộc và gần gũi với bạn đọc khắp năm châu như: “Cô bé bán diêm, Bầy chim thiên nga, Nàng tiên cá, Bộ quần áo mới cho hoàng đế,…”'),
(31, 'Hoàng Minh Phương', '1976-08-06', 0, 32432432432, 'Là một trong số những hot girl HV Hàng không xinh đẹp, nổi bật nhất nhưng Lê Hoàng Minh Phương lại có niềm đam mê nghệ thuật, đặc biệt là phim ảnh.'),
(32, 'Joanne \"Jo\" Rowling', '1965-07-31', 0, 4142341232, 'Cư ngụ tại thủ đô Edinburgh,Scotland là tiểu thuyết gia người Anh, tác giả bộ truyện giả tưởng nổi tiếng Harry Potter với bút danh J. K. Rowling.[6]'),
(33, '	Mark Goulston', '1922-04-01', 1, 3424324, 'chuyên viên kỹ năng giao tiếp  cuộc sống'),
(34, 'Tô Hoài', '1920-09-27', 1, 23213213213, ' là một nhà văn Việt Nam. Một số tác phẩm đề tài thiếu nhi của ông được dịch ra ngoại ngữ. Ông được nhà nước Việt Nam trao tặng Giải thưởng Hồ Chí Minh về Văn học - Nghệ thuật Đợt 1 (1996) cho các tác phẩm: Xóm giếng, Nhà nghèo, O chuột, Dế mèn phiêu lưu ký, Núi Cứu quốc, Truyện Tây Bắc, Mười năm, Xuống làng, Vỡ tỉnh, Tào lường, Họ Giàng ở Phìn Sa, Miền Tây, Vợ chồng A Phủ, Tuổi trẻ Hoàng Văn Thụ.'),
(35, 'Nguyễn Văn Thạc', '1949-05-19', 1, 3243243243, 'Tiểu sử của liệt sĩ Nguyễn Văn Thạc'),
(36, 'Quốc Việt', '1982-11-05', 1, 2321321323, 'lập trình viên chuyên c'),
(37, 'Nguyễn Văn Tuấn', '1977-09-03', 1, 4343243434, 'Chuyên viên phân tích về dữ liệu'),
(38, 'Dominic utton', '1987-07-10', 1, 34234234234, 'Chào mừng bạn đến với trang web của tôi!\r\n\r\nTôi là một nhà báo tự do và tác giả của 20 năm kinh nghiệm có trụ sở tại London và Oxford, Anh. \r\n\r\nTôi viết và chỉnh sửa các tính năng và nhận xét và viết sách về nhiều chủ đề bao gồm lối sống, âm nhạc, thể thao, giải trí và tội phạm. \r\n\r\nTác phẩm của tôi đã được lọt vào danh sách đề cử cho Giải thưởng Báo chí Anh. \r\n\r\nCuốn tiểu thuyết mới nhất của tôi, Dead End Close, được xuất bản bởi The Odyssey Press. Cuốn tiểu thuyết đầu tiên của tôi, Sự đánh giá thời gian của Martin Harbottle, đã được Oneworld xuất bản vào tháng 1 năm 2014. '),
(39, 'Vũ Gia Tê', '1979-10-08', 0, 234543534, 'giáo sư tiến sĩ chuyên toán');

